# backend/models.py
from sqlalchemy import Column, Integer, String, Boolean, Float, ForeignKey, DateTime, func
from database import Base
from datetime import datetime

class User(Base):
    __tablename__ = "users"

    id = Column(Integer, primary_key=True, index=True)
    email = Column(String, unique=True, index=True, nullable=False)
    name = Column(String, nullable=False)
    hashed_password = Column(String, nullable=False)
    is_admin = Column(Boolean, default=False)
    is_active = Column(Boolean, default=True)
    created_at = Column(DateTime(timezone=True), server_default=func.now())
    updated_at = Column(DateTime(timezone=True), server_default=func.now(), onupdate=func.now())

class AdminVerificationToken(Base):
    __tablename__ = "admin_verification_tokens"

    id = Column(Integer, primary_key=True, index=True)
    email = Column(String, unique=True, index=True, nullable=False)
    token = Column(String, unique=True, index=True, nullable=False)
    expires_at = Column(DateTime(timezone=True), nullable=False)
    used = Column(Boolean, default=False)
    created_at = Column(DateTime(timezone=True), server_default=func.now())

class RouteInfo(Base):
    __tablename__ = "route_info"  # ← Changed from "route_infos" to "route_info"

    id = Column(Integer, primary_key=True, index=True)
    route_name = Column(String, unique=True, index=True, nullable=False)
    coordinates = Column(String, nullable=False)  # JSON string
    stops = Column(String, nullable=False)         # JSON string
    created_at = Column(DateTime(timezone=True), server_default=func.now())
    updated_at = Column(DateTime(timezone=True), server_default=func.now(), onupdate=func.now())

class Bus_info(Base):
    __tablename__ = "bus_info"

    id = Column(Integer, primary_key=True, index=True)
    bus_number = Column(String, unique=True, index=True)
    route_id = Column(Integer, ForeignKey("route_info.id"))  # ← Now matches correctly
    latitude = Column(Float)
    longitude = Column(Float)
    last_stop_id = Column(Integer, nullable=True)
    next_stop_id = Column(Integer, nullable=True)
    direction_ref = Column(Integer, default=0)
    updated_at = Column(DateTime, default=datetime.utcnow, onupdate=datetime.utcnow)
# NEW: Simulation configuration for Ring Road
class RingRoadSimulationConfig(Base):
    __tablename__ = "ring_road_simulation_config"
    id = Column(Integer, primary_key=True, index=True)
    stop_waiting_seconds = Column(Integer, default=30)  # seconds to wait at each stop
    average_speed_kmh = Column(Float, default=25.0)     # fallback speed if model fails
    created_at = Column(DateTime(timezone=True), server_default=func.now())














